import React, { useState, useEffect } from 'react';
import ReactMapGl, { Marker, Popup } from 'react-map-gl';
import * as Data from './data.json';
import './Mapbox.css';






function Mapbox() {
    const [viewPort, setviewPort] = useState({
        latitude: 45.4211,
        longitude: -75.6903,
        width: "100vw",
        height: "100vh",
        zoom: 10,
    });

    const [selectedPark, setSelectedPark] = useState(null);



    return (
        <div>
            <ReactMapGl {...viewPort} mapboxApiAccessToken={process.env.REACT_APP_MAP_ACCESS_TOKEN}
                onViewportChange={(viewPort) => { setviewPort(viewPort) }} mapStyle="mapbox://styles/nabanit/cka7uauty1vpl1ip2f33744yz">
                {Data.features.map((park) => (
                    <Marker key={park.properties.PARK_ID} latitude={park.geometry.coordinates[1]} longitude={park.geometry.coordinates[0]}>
                        <button className="marker-btn" onMouseOver={e => {
                            e.preventDefault();
                            setSelectedPark(park);
                        }}>
                            <img src="/mapmarker.png" alt="park" />
                        </button>

                    </Marker>
                ))}
                {selectedPark ? (
                    <Popup latitude={selectedPark.geometry.coordinates[1]} longitude={selectedPark.geometry.coordinates[0]}
                        onClose={() => (setSelectedPark(null))}>
                        <div>
                            <p>{selectedPark.properties.NAME}</p>
                            <p>latitude={selectedPark.geometry.coordinates[1]},longitude={selectedPark.geometry.coordinates[0]}</p>

                        </div>
                    </Popup>
                ) : null}
            </ReactMapGl >
        </div >
    )

}

export default Mapbox;
