import React, { Component } from 'react';
import axios from 'axios';



export default class TotalData extends Component {
    constructor() {
        super();
        this.state = {
            totalData: [],
            isfinalData: false
        }
    }
    componentDidMount() {
        axios.get('https://nepalcorona.info/api/v1/data/nepal')
            .then(res => {
                this.setState({
                    totalData: res.data,
                    isfinalData: false
                })
            })
    }
    render() {
        console.log("final data>>", this.state);
        const { totalData, isfinalData } = this.state;
        let test = totalData.map((item, deaths) => (
            <div>
                <p>{item.deaths}</p>
            </div>

        ))
        let finalData = isfinalData
            ? test
            : <p>Isloading</p>
        return (
            <div>
                <finalData />

            </div>
        )
    }

}
