import React, { Component } from 'react';
import ReactMapGl, { Marker, Popup } from 'react-map-gl';
import './Mapbox.css';


export default class Mapbox extends Component {
    constructor() {
        super();
        this.state = {
            viewport: {
                latitude: 27.713264,
                longitude: 85.322985,
                width: "100vw",
                height: "100vh",
                zoom: 10,
            }
        }
    }
    viewportChange = (e) => {
        const { name, value } = e.target;
        this.setState({
            [name]: value
        })
    }
    render() {
        const { viewport } = this.state;
        return (
            <div>
                <ReactMapGl {...viewport} mapboxApiAccessToken={process.env.REACT_APP_MAP_ACCESS_TOKEN} mapStyle="mapbox://styles/nabanit/cka7uauty1vpl1ip2f33744yz" onViewportChange={this.viewportChange}>
                    <h2>Hello</h2>
                </ReactMapGl>

            </div>
        )
    }
}
