import React, { useState, useEffect } from 'react';
import ReactMapGl, { Marker, Popup } from 'react-map-gl';
import { coronaNepal } from './../src/httpBrowsing';
import './Mapbox.css';
import * as Data from './data.json';







function Mapbox() {
    const [viewPort, setviewPort] = useState({
        latitude: 27.713264,
        longitude: 85.322985,
        width: "100vw",
        height: "100vh",
        zoom: 10,
    });

    const [selectedHotel, setselectedHotel] = useState();





    return (
        <div>
            <ReactMapGl {...viewPort} mapboxApiAccessToken={process.env.REACT_APP_MAP_ACCESS_TOKEN}
                onViewportChange={(viewPort) => { setviewPort(viewPort) }} mapStyle="mapbox://styles/nabanit/cka7uauty1vpl1ip2f33744yz">

                {Data.data.map((hotel) => (
                    <Marker key={hotel.properties.HOTEL_ID} latitude={hotel.geometry.coordinates[1]} longitude={hotel.geometry.coordinates[0]}>
                        <button className="marker-btn" onMouseOver={e => {
                            e.preventDefault();
                            setselectedHotel(hotel);
                        }}>
                            <img src="/mapmarker.png" alt="park" />
                        </button>
                    </Marker>
                ))}
            </ReactMapGl >
        </div >
    )

}

export default Mapbox;
