import React, { useState, useEffect } from 'react';
import ReactMapGl, { Marker, Popup } from 'react-map-gl';
import * as Data from './data.json';
import './Mapbox.css';






function Mapbox() {
    const [viewPort, setviewPort] = useState({
        latitude: 27.713264,
        longitude: 85.322985,
        width: "100vw",
        height: "100vh",
        zoom: 10,
    });

    const [selectedHotel, setselectedHotel] = useState(null);
    useEffect(() => {
        const listner = e => {
            if (e.key === "Escape") {
                setselectedHotel(null);
            }
        }
        window.addEventListener("keydown", listner);
        return () => {
            window.removeEventListener("keydown", listner);
        };

    }, []);




    return (
        <div>
            <ReactMapGl {...viewPort} mapboxApiAccessToken={process.env.REACT_APP_MAP_ACCESS_TOKEN}
                onViewportChange={(viewPort) => { setviewPort(viewPort) }} mapStyle="mapbox://styles/nabanit/cka7uauty1vpl1ip2f33744yz">
                {Data.features.map((hotel) => (
                    <Marker key={hotel.properties.HOTEL_ID} latitude={hotel.geometry.coordinates[1]} longitude={hotel.geometry.coordinates[0]}>
