                        onClose={() => (setselectedHotel(null))}>
                        <div>
                            <p>{selectedHotel.properties.NAME}</p>
                            <p>latitude={selectedHotel.geometry.coordinates[1]},longitude={selectedHotel.geometry.coordinates[0]}</p>

                        </div>
                    </Popup>
                ) : null}
            </ReactMapGl >
        </div >
    )

}

export default Mapbox;
