import React, { useState, useEffect } from 'react';
import ReactMapGl, { Marker, Popup } from 'react-map-gl';
import { coronaNepal } from './../src/httpBrowsing';
import './Mapbox.css';
import * as Data from './data.json';
import axios from 'axios';







function Mapbox() {
    const [viewPort, setviewPort] = useState({
        latitude: 27.713264,
        longitude: 85.322985,
        width: "100vw",
        height: "100vh",
        zoom: 10,
    });

    const [posts, setposts] = useState([]);
    useEffect(() => {
        axios.get('https://data.nepalcorona.info/api/v1/covid')
            .then(res => {
                console.log("what is this>>", res);
                const finalArray = [...res.data]
                setposts(finalArray);
            })
            .catch(err => {
                console.log(err);
            })
    }, [])




    return (
        <div>
            <ReactMapGl {...viewPort} mapboxApiAccessToken={process.env.REACT_APP_MAP_ACCESS_TOKEN}
                onViewportChange={(viewPort) => { setviewPort(viewPort) }} mapStyle="mapbox://styles/nabanit/cka7uauty1vpl1ip2f33744yz">

                {posts.map((municipality) => (
                    <Marker key={municipality.id} latitude={municipality.centroid.coordinates[1]} longitude={municipality.centroid.coordinates[0]}>
                        <button className="marker-btn" onMouseOver={e => {
                            e.preventDefault();
                            setposts(municipality);
                        }}>
                            <img src="/mapmarker.png" alt="park" />
                        </button>

                    </Marker>
                ))}
            </ReactMapGl >
        </div >
    )

}

export default Mapbox;
