import React, { useState, useEffect } from 'react';
import CanvasJS from 'canvasjs';
import axios from 'axios';
import ReactMapGl, { Marker, Popup } from 'react-map-gl';
import Sidebar from '../Contents/Sidebar';
import Footer from '../Contents/Footer';
import Header from '../Contents/Header';



export default function CoronaNepal() {




    const [coronaNepal, setcoronaNepal] = useState([]);
    const [selectedPerson, setselectedPerson] = useState(null);
    const [provinence, setprovinence] = useState([]);
    const [totalData, setTotalData] = useState([]);
    const [isfinalData, setIsfinalData] = useState(false);
    const [Hospital, setHospital] = useState([]);


    useEffect(() => {
        axios.get('https://data.nepalcorona.info/api/v1/covid')
            .then(res => {
                console.log("what is this>>", res);
                setcoronaNepal(res.data)
                setprovinence(res.data)

            })
            .catch(err => {
                console.log(err);
            })

        axios.get('https://nepalcorona.info/api/v1/data/nepal')
            .then(res => {
                setTotalData([res.data])
                setIsfinalData(true)
            })
            .catch(err => {
                console.log("error>>", err);
            })
        axios.get('https://nepalcorona.info/api/v1/hospitals')
            .then(res => {
                setHospital(res.data.data)


            })
            .catch(err => {
                console.log("error>>", err);
            })

    }, [])
    const options = {
        animationEnabled: true,
        title: {
            text: "Monthly Sales - 2017"
        },
        axisX: {
            valueFormatString: "MMM"
        },
        axisY: {
            title: "Sales (in USD)",
            prefix: "$",
            includeZero: false
        },
        data: [{
            yValueFormatString: "$#,###",
            xValueFormatString: "MMMM",
            type: "spline",
            dataPoints: [
                { x: new Date(2017, 0), y: 25060 },
                { x: new Date(2017, 1), y: 27980 },
                { x: new Date(2017, 2), y: 42800 },
                { x: new Date(2017, 3), y: 32400 },
                { x: new Date(2017, 4), y: 35260 },
                { x: new Date(2017, 5), y: 33900 },
                { x: new Date(2017, 6), y: 40000 },
                { x: new Date(2017, 7), y: 52500 },
                { x: new Date(2017, 8), y: 32300 },
                { x: new Date(2017, 9), y: 42000 },
                { x: new Date(2017, 10), y: 37160 },
                { x: new Date(2017, 11), y: 38400 }
            ]
        }]
    }

    console.log("Hospitals list >>", Hospital);
    console.log("corona", coronaNepal);



    return (
        <div>
            <Header />
            <div>

                {/* Content Wrapper. Contains page content */}
                <div className="content-wrapper">
                    {/* Content Header (Page header) */}
                    <div className="content-header">
                        <div className="container-fluid">

                        </div>{/* /.container-fluid */}
                    </div>
                    {/* /.content-header */}
                    {/* Main content */}
                    <section className="content">
                        <div className="container-fluid">
                            {/* Info boxes */}
                            <div className="row">
                                <div className="col-12 col-sm-6 col-md-3">
