import React, { Component } from 'react';
import ReactMapGl, { Marker, Popup } from 'react-map-gl';
import { coronaNepal } from './../src/httpBrowsing';
import './Mapbox.css';
import * as Data from './data.json';
import axios from 'axios';






export default class Mapbox extends Component {
    constructor() {
        super();
        this.state = {
            viewPort: {
                latitude: 27.713264,
                longitude: 85.322985,
                width: "100vw",
                height: "100vh",
                zoom: 10
            },
            corona: {},
            finalCorona: []
        }

    }
    componentDidMount() {
        axios.get('https://data.nepalcorona.info/api/v1/covid')
            .then(res => {

                this.setState({
                    corona: res.data

                })
                console.log(res);
                console.log("finally>>", this.state);
            })
    }
    handleviewportChange = (e) => {
        const { name, value } = e.target;
        this.setState({
            viewPort: {
                [name]: value
            }
        })
    }

    render() {
        const { viewPort } = this.state;
        return (
            <div>
                <ReactMapGl {...viewPort} mapboxApiAccessToken={process.env.REACT_APP_MAP_ACCESS_TOKEN}
                    onViewportChange={this.handleviewportChange} name="viewPort" mapStyle="mapbox://styles/nabanit/cka7uauty1vpl1ip2f33744yz">


                </ReactMapGl >
            </div >
        )

    }
}
